export function validateEmail(e){return/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(e)}export function validatePhoneNumber(e){return/^(\+\d{1,3}\s?)?(\(\d{1,3}\)\s?)?(\d{4}[-\s]?\d{4}|\d{8}|\(\d{3}\)[-.\s]?\d{3}[-.\s]?\d{4}|\(\d{3}\)[-.\s]?\d{4}[-\s]?\d{4})$/.test(e)}export function validateRequired(e){return""!==e.trim()}export function validateNumber(e){return!isNaN(e)}export function validateDate(e){return/^\d{4}-\d{2}-\d{2}$/.test(e)}export function validateForm(e,r=[]){for(const t of r){const r=e.querySelector(t.selector).value.trim();if(t.required&&!validateRequired(r))return alert("Campo requerido",t.errorMessage,"warning","Ok",!0),!1;if("email"===t.type&&!validateEmail(r))return alert("Campo requerido","El correo electrónico no es válido.","warning","Ok",!0),!1;if("phone"===t.type&&!validatePhoneNumber(r))return alert("Campo requerido","El número de teléfono es incorrecto.","warning","Ok",!0),!1;if("number"===t.type&&!validateNumber(r))return alert("Campo requerido","El valor debe ser un número.","warning","Ok",!0),!1}return!0}export function isEqual(e,r){return e===r}